mapper.insert:
		
			SELECT LAST_INSERT_ID() AS id
		
		
		
			INSERT INTO TB_ROUTE_INSPECT_WIND 
			(
	          C_RECORD_TIME
	        , C_ATTACHMENT_NAME
	        , C_UNIT_ID
	        , C_ATTACHMENT_URL
	        , C_RL_ID
	        , C_RECORD_CONTENT
	        , C_FZR
	        , C_RECORD_TYPE
	        , C_FIND_QUESTION
			)
			VALUES
			(
	         #{recordTime}
           , #{attachmentName}
           , #{unitId}
           , #{attachmentUrl}
           , #{rlId}
           , #{recordContent}
           , #{fzr}
           , #{recordType}
           , #{findQuestion}
			)

mapper.update:
			
				UPDATE TB_ROUTE_INSPECT_WIND SET
                   C_RECORD_TIME=#{recordTime}
	             , C_ATTACHMENT_NAME=#{attachmentName}
	             , C_UNIT_ID=#{unitId}
	             , C_ATTACHMENT_URL=#{attachmentUrl}
	             , C_RL_ID=#{rlId}
	             , C_RECORD_CONTENT=#{recordContent}
	             , C_FZR=#{fzr}
	             , C_RECORD_TYPE=#{recordType}
	             , C_FIND_QUESTION=#{findQuestion}
				WHERE C_ID=#{id}
